--select * from #TEMP_IN order by ITSABillingCat

-- Create the second temp VIEW.
--Declare @SQL_statement varchar(1000)
set @SQL_statement = 'Create View dbo.SP_Get_AFE_Summary_Airline_View2 AS select dbo.lkITSABillingCategory.Description AS ITSABillingCat, FTE_Approved_Time.* from FTE_Approved_Time 
LEFT OUTER JOIN	dbo.tblAFEDetail ON FTE_Approved_Time.AFE_DescID = dbo.tblAFEDetail.AFE_DescID 
LEFT OUTER JOIN dbo.lkITSABillingCategory ON dbo.tblAFEDetail.ITSABillingCategoryID = dbo.lkITSABillingCategory.ITSABillingCategoryID 
where Appr_FTE_Hours > 0 and CurrentMonth = ''201608'' '
exec (@SQL_statement)

-- Copy the data from the second temp VIEW into #TEMP_IN.
insert #TEMP_IN (AFEDesc, Program, ProgramGroup, Prog_GroupID, ProgramID, Appr_FTE_Hours, CurrentMonth, AFE_DescID, Funding_CatID, COBusinessLead, UA_VicePresident, ITSABillingCat )
select AFEDesc, Program, ProgramGroup, Prog_GroupID, ProgramID, Appr_FTE_Hours, CurrentMonth, AFE_DescID, Funding_CatID, COBusinessLead, UA_VicePresident, ITSABillingCat 
from dbo.SP_Get_AFE_Summary_Airline_View2
	
