			select @ITSABillingCat = ITSABillingCat from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID 

			-- Populate the Hours by the new Billing Type.
			declare @JrSEOnshore decimal(10,2), @JrSEOffshore decimal(10,2), @MidSEOnshore decimal(10,2), @MidSEOffshore decimal(10,2), @AdvSEOnshore decimal(10,2), @AdvSEOffshore decimal(10,2), @SenSEOnshore decimal(10,2), @SenSEOffshore decimal(10,2), @ConsArchOnshore decimal(10,2), @ConsArchOffshore decimal(10,2), @ProjLeadOnshore decimal(10,2), @ProjLeadOffshore decimal(10,2), @ProjMgrOnshore decimal(10,2), @ProjMgrOffshore decimal(10,2), @ProgMgrOnshore decimal(10,2), @ProgMgrOffshore decimal(10,2), @JrProjMgrOffshore decimal(10,2), @JrProgMgrOffshore decimal(10,2), @SrProjMgrOffshore decimal(10,2), @SrProgMgrOffshore decimal(10,2)
			declare @FTPStaffAugOnshore decimal(10,2), @FTPStaffAugOffshore decimal(10,2)
		
			select @JrSEOnshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Jr SE%' and Onshore = 1
			select @JrSEOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Jr SE%' and Offshore = 1
			select @MidSEOnshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Mid SE%' and Onshore = 1
			select @MidSEOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Mid SE%' and Offshore = 1
			select @AdvSEOnshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Adv SE%' and Onshore = 1
			select @AdvSEOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Adv SE%' and Offshore = 1
			select @SenSEOnshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Sen SE%' and Onshore = 1
			select @SenSEOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Sen SE%' and Offshore = 1
			select @ConsArchOnshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Cons%' and Onshore = 1
			select @ConsArchOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Cons%' and Offshore = 1
			select @ProjLeadOnshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Project Lead%' and Onshore = 1
			select @ProjLeadOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Project Lead%' and Offshore = 1
				
			select @ProjMgrOnshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType in ('Project Mgr','Common Project Mgr','Premium Project Mgr','Niche Project Mgr','Off SHR Project Mgr','Legacy Project Mgr','Program Project Mgr') and Onshore = 1
			select @ProjMgrOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType in ('Project Mgr','Common Project Mgr','Premium Project Mgr','Niche Project Mgr','Off SHR Project Mgr','Legacy Project Mgr','Program Project Mgr') and Offshore = 1				
			select @ProgMgrOnshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType in ('Program Mgr','Common Program Mgr','Premium Program Mgr','Niche Program Mgr','Off SHR Program Mgr','Legacy Program Mgr','Program Mgr') and Onshore = 1	
			select @ProgMgrOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType in ('Program Mgr','Common Program Mgr','Premium Program Mgr','Niche Program Mgr','Off SHR Program Mgr','Legacy Program Mgr','Program Mgr') and Offshore = 1	
				
			select @JrProjMgrOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Common Jr Project Mgr%' and Offshore = 1	
			select @JrProgMgrOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Common Jr Program Mgr%' and Offshore = 1	
			select @SrProjMgrOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Common Sr Project Mgr%' and Offshore = 1	
			select @SrProgMgrOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType like '%Common Sr Program Mgr%' and Offshore = 1	

			if ( @ITSABillingCatSelection = '-1' )
				begin
				select @FTPStaffAugOnshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType = 'FTP Staff Aug' and Onshore = 1
				select @FTPStaffAugOffshore = sum(isnull(Hours,0)) from #TEMP_IN where Prog_GroupID = @CurProg_GroupID and ProgramID = @CurProgramID and AFE_DescID = @CurAFE_DescID and 
				NewBillingType = 'FTP Staff Aug' and Offshore = 1				
				end
												
