       		set @@out_fundingcat = NULL
       		set @@out_afenumber = NULL
			set @@out_programmgr = NULL
			set @@out_location = NULL
       		set @@Total_FTE = NULL
			exec GET_Location_Combo @CurAFE_DescID, @DateFrom, @DateTo, @@out_location OUTPUT, @@out_fundingcat OUTPUT, @@out_afenumber OUTPUT,@@out_programmgr OUTPUT, @@Total_FTE OUTPUT
               
			-- Update temporary output table #TEMP_OUT.   
			if ( @ITSABillingCatSelection = '-1' )
				begin 
				update #TEMP_OUT set ITSABillingCat = @ITSABillingCat, fundingcat = @@out_fundingcat, AFENumber = @@out_afenumber, ProgramMgr = @@out_programmgr, location = @@out_location, COApprovedFTEs = isnull(@@Total_FTE,0), JrSEOnshoreHours = isnull(@JrSEOnshore,0), JrSEOffshoreHours = isnull(@JrSEOffshore,0), MidSEOnshoreHours = isnull(@MidSEOnshore,0), MidSEOffshoreHours = isnull(@MidSEOffshore,0), AdvSEOnshoreHours = isnull(@AdvSEOnshore,0), AdvSEOffshoreHours = isnull(@AdvSEOffshore,0), SenSEOnshoreHours = isnull(@SenSEOnshore,0), SenSEOffshoreHours = isnull(@SenSEOffshore,0), ConsArchOnshoreHours = isnull(@ConsArchOnshore,0), ConsArchOffshoreHours = isnull(@ConsArchOffshore,0), ProjLeadOnshoreHours = isnull(@ProjLeadOnshore,0), ProjLeadOffshoreHours = isnull(@ProjLeadOffshore,0), ProjMgrOnshoreHours = isnull(@ProjMgrOnshore,0), ProjMgrOffshoreHours = isnull(@ProjMgrOffshore,0), ProgMgrOnshoreHours = isnull(@ProgMgrOnshore,0), ProgMgrOffshoreHours = isnull(@ProgMgrOffshore,0), JrProjMgrOffshoreHours = isnull(@JrProjMgrOffshore,0), JrProgMgrOffshoreHours = isnull(@JrProgMgrOffshore,0), SrProjMgrOffshoreHours = isnull(@SrProjMgrOffshore,0), SrProgMgrOffshoreHours = isnull(@SrProgMgrOffshore,0), FTPStaffAugOnshoreHours = isnull(@FTPStaffAugOnshore,0), FTPStaffAugOffshoreHours = isnull(@FTPStaffAugOffshore,0)
				where AutoKey = @MaxAFEDesc				
				end
			else
				begin
				update #TEMP_OUT set ITSABillingCat = @ITSABillingCat, fundingcat = @@out_fundingcat, AFENumber = @@out_afenumber, ProgramMgr = @@out_programmgr, location = @@out_location, COApprovedFTEs = isnull(@@Total_FTE,0), JrSEOnshoreHours = isnull(@JrSEOnshore,0), JrSEOffshoreHours = isnull(@JrSEOffshore,0), MidSEOnshoreHours = isnull(@MidSEOnshore,0), MidSEOffshoreHours = isnull(@MidSEOffshore,0), AdvSEOnshoreHours = isnull(@AdvSEOnshore,0), AdvSEOffshoreHours = isnull(@AdvSEOffshore,0), SenSEOnshoreHours = isnull(@SenSEOnshore,0), SenSEOffshoreHours = isnull(@SenSEOffshore,0), ConsArchOnshoreHours = isnull(@ConsArchOnshore,0), ConsArchOffshoreHours = isnull(@ConsArchOffshore,0), ProjLeadOnshoreHours = isnull(@ProjLeadOnshore,0), ProjLeadOffshoreHours = isnull(@ProjLeadOffshore,0), ProjMgrOnshoreHours = isnull(@ProjMgrOnshore,0), ProjMgrOffshoreHours = isnull(@ProjMgrOffshore,0), ProgMgrOnshoreHours = isnull(@ProgMgrOnshore,0), ProgMgrOffshoreHours = isnull(@ProgMgrOffshore,0), JrProjMgrOffshoreHours = isnull(@JrProjMgrOffshore,0), JrProgMgrOffshoreHours = isnull(@JrProgMgrOffshore,0), SrProjMgrOffshoreHours = isnull(@SrProjMgrOffshore,0), SrProgMgrOffshoreHours = isnull(@SrProgMgrOffshore,0)
				where AutoKey = @MaxAFEDesc				
				end

		FETCH NEXT FROM AFEDesc_cursor INTO @CurAFEDesc, @CurAFE_DescID, @CurCOBusinessLead
		END    
		CLOSE AFEDesc_cursor
		DEALLOCATE AFEDesc_cursor

	------------------------------------------------------------------------------------
	FETCH NEXT FROM Program_cursor INTO @CurProgram, @CurProgramID
	END
	CLOSE Program_cursor
	DEALLOCATE Program_cursor
	
	---------------------------------------------------------------------------------------------------------------------------
       -- Populate TotalHours Column for record type 30 (horizontal add up).
	if ( @ITSABillingCatSelection = '-1' )
